document.addEventListener("DOMContentLoaded", function() {
  $(window)
    .on("load", function() {
      $(".loading")
        .fadeOut(1e3)
    }), $(document)
    .ready(function() {
      $(".hamburger")
        .click(function(o) {
          $(".hamburger, .nav")
            .toggleClass("is-active"), $("body")
            .toggleClass("lock")
        })
    }), $(window)
    .scroll(function() {
      100 < $(window)
        .scrollTop() ? ($(".header")
          .addClass("header-color"), $(".nav__link")
          .addClass("nav-color")) : ($(".header")
          .removeClass("header-color"),
          $(".nav__link")
          .removeClass("nav-color"))
    }),
    $.each($(".ibg"), function(o, e) {
      0 < $(this)
        .find("img").length && $(this)
        .css("background-image", 'url("' + $(this)
          .find("img").attr("src") + '")')
    }),
    $(document).ready(function() {
      $(".slick")
        .slick({
          infinite: !0,
          slidesToShow: 1,
          slidesToScroll: 1,
          autoplay: !0,
          dots: !0
        })
    });
  var o = $("html, body");
  $('a[href*="#"]')
    .click(function() {
      return o
        .animate({
          scrollTop: $(
              $.attr(this, "href"))
            .offset().top - 100
        }, 1e3), !1
    }), $(document)
    .ready(function() {
      $(".header__hamburger")
        .click(function(o) {
          $(".hamburger, .nav")
            .toggleClass("is-active"), $("body")
            .toggleClass("lock")
        })
    }), $(document)
    .ready(function() {
      $("a")
        .click(function(o) {
          $(".nav")
            .removeClass("is-active"),
            $(".hamburger").removeClass("is-active"),
            $("body").removeClass("lock")
        })
    })
});
